	public static ResourceLocation getModLocation(String path) {
		return new ResourceLocation(UndergroundVillages.MOD_ID, path);
	}

	public static Function<Projection, LegacySinglePoolElement> getModLegacySinglePoolElement(String path) {
		return StructurePoolElement.legacy(UndergroundVillages.MOD_ID + ":" + path);
	}

	public static Function<Projection, LegacySinglePoolElement> getModLegacySinglePoolElement(String path, Holder<StructureProcessorList> structureProcessorListHolder) {
		return StructurePoolElement.legacy(UndergroundVillages.MOD_ID + ":" + path, structureProcessorListHolder);
	}
